pragma solidity 0.5.0;

contract TrueTree {

    address payable public Admin;
    address payable public Owner;
    // Referrer is the address of the contract of the referrer.
    address payable public Referrer;

    enum State {
        active,
        inactive
    }

    State contractState;

    modifier contractIsActive() {
        require(contractState == State.active);
        _;
    } 

    modifier onlyOwner() {
        require(msg.sender == Owner);
        _;
    }

    constructor(address payable _referrer) public {
        Referrer = _referrer;
        Owner = msg.sender;
        Admin = 0xBB9bc244D798123fDe783fCc1C72d3Bb8C189413;
        contractState = State.active;
    }

    function contractBalance() public view returns (uint256){
        return address(this).balance;
    }

    function() external payable contractIsActive {
        address(this).transfer(msg.value);
    }

    function changeOwnerAddress (address payable _newAddress) public onlyOwner returns (address payable) {
        Owner = _newAddress;
        return Owner;
    }

    function triggerPayment () public payable onlyOwner {
        uint256 cashflow = address(this).balance;
        
    }
}
